mapper.select:
SELECT


,jianshenkecheng.jiaolian_id as jianshenkechengJiaolianId
,jianshenkecheng.jianshenkecheng_name as jianshenkechengName
,jianshenkecheng.jianshenkecheng_photo as jianshenkechengPhoto
,jianshenkecheng.jianshenkecheng_video as jianshenkechengVideo
,jianshenkecheng.zan_number as zanNumber
,jianshenkecheng.cai_number as caiNumber
,jianshenkecheng.jianshenkecheng_types as jianshenkechengTypes
,jianshenkecheng.jianshenkecheng_clicknum as jianshenkechengClicknum
,jianshenkecheng.jianshenkecheng_content as jianshenkechengContent
,jianshenkecheng.data_delete as dataDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.new_money as newMoney
,yonghu.data_delete as dataDelete

FROM jianshenkecheng_collection  a
left JOIN jianshenkecheng jianshenkecheng ON a.jianshenkecheng_id = jianshenkecheng.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.jianshenkecheng_id = #{params.jianshenkechengId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.jianshenkecheng_collection_types = #{params.jianshenkechengCollectionTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}




        and a.jianshenkecheng_id IS NOT NULL


        and jianshenkecheng.jiaolian_id = #{params.jiaolianId}


        and jianshenkecheng.jianshenkecheng_name like CONCAT('%',#{params.jianshenkechengName},'%')


        and jianshenkecheng.jianshenkecheng_video like CONCAT('%',#{params.jianshenkechengVideo},'%')


          and jianshenkecheng.zan_number >= #{params.zanNumberStart}   


          and jianshenkecheng.zan_number <= #{params.zanNumberEnd}   


        and jianshenkecheng.zan_number = #{params.zanNumber}


          and jianshenkecheng.cai_number >= #{params.caiNumberStart}   


          and jianshenkecheng.cai_number <= #{params.caiNumberEnd}   


        and jianshenkecheng.cai_number = #{params.caiNumber}


        and jianshenkecheng.jianshenkecheng_types = #{params.jianshenkechengTypes}



          and jianshenkecheng.jianshenkecheng_clicknum >= #{params.jianshenkechengClicknumStart}   


          and jianshenkecheng.jianshenkecheng_clicknum <= #{params.jianshenkechengClicknumEnd}   


        and jianshenkecheng.jianshenkecheng_clicknum = #{params.jianshenkechengClicknum}


        and jianshenkecheng.jianshenkecheng_content like CONCAT('%',#{params.jianshenkechengContent},'%')


          and jianshenkecheng.data_delete >= #{params.dataDeleteStart}   


          and jianshenkecheng.data_delete <= #{params.dataDeleteEnd}   


        and jianshenkecheng.data_delete = #{params.dataDelete}


          and UNIX_TIMESTAMP(jianshenkecheng.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(jianshenkecheng.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


        and a.new_money = #{params.newMoney}


          and yonghu.data_delete >= #{params.dataDeleteStart}   


          and yonghu.data_delete <= #{params.dataDeleteEnd}   


        and yonghu.data_delete = #{params.dataDelete}


          and UNIX_TIMESTAMP(yonghu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(yonghu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}


order by a.${params.sort} ${params.order}
