mapper.select:
SELECT



FROM jiaolian  a



        and a.id in

        #{item}



        and a.username like CONCAT('%',#{params.username},'%')


        and a.password like CONCAT('%',#{params.password},'%')


        and a.jiaolian_name like CONCAT('%',#{params.jiaolianName},'%')


        and a.jiaolian_phone like CONCAT('%',#{params.jiaolianPhone},'%')


        and a.sex_types = #{params.sexTypes}


        and a.jiaolian_email like CONCAT('%',#{params.jiaolianEmail},'%')


        and a.jiaolian_content like CONCAT('%',#{params.jiaolianContent},'%')


          and a.data_delete >= #{params.dataDeleteStart}   


          and a.data_delete <= #{params.dataDeleteEnd}   


        and a.data_delete = #{params.dataDelete}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}



order by a.${params.sort} ${params.order}
